"use strict";function startTime(){var today=new Date,h=today.getHours(),m=today.getMinutes(),s=today.getSeconds();m=checkTime(m),s=checkTime(s),document.getElementById("txt").innerHTML=h+":"+m+":"+s;setTimeout(function(){startTime()},500)}function checkTime(i){return 10>i&&(i="0"+i),i}function show(){new Notification("Break Time!",{body:"Time to get away from the computer for a minute"});return!1}var Notification=window.Notification||window.mozNotification||window.webkitNotification;Notification.requestPermission(function(){});